    $.ajax({
      type: "post",
        url: K('space').attributes("Kinetic Billing Server URL")+"/billingInfo",
        data: JSON.stringify({ 
          customerId: bridgedData['Billing Customer Reference'],
          space: K('space').slug(),
          billingService: K('space').attributes("Billing Company"),
        }),
        headers: {
          'Accept': 'application/json, text/plain, */*',
          'Content-Type': 'application/json;charset=UTF-8'
      	}
      })
      .done(function( data ) {
        if (data.data===null) {
  	      K('field[The last debit will be taken on]').value("");
          K('field[Billing Period]').value("");
        } else {    
        var period="months";
        if (paymentPeriod==="Daily") {
          period="days";
        } else if (paymentPeriod==="Weekly" || paymentPeriod==="Fortnightly") {
          period="weeks"; 
        } else if (paymentPeriod==="Monthly") {
          period="months"; 
        } 
        var tomorrow = new Date((new Date()).getTime() + 24 * 60 * 60 * 1000);
        var nextBillingDate=undefined;
        if (moment(bridgedData['Billing Start Date'],"YYYY-MM-DD").isAfter(moment())||moment(bridgedData['Billing Start Date'],"YYYY-MM-DD").isSame(moment().hour(0).minute(0).second(0).millisecond(0))){
          nextBillingDate=moment(bridgedData['Billing Start Date'],"YYYY-MM-DD");
        } else {
          nextBillingDate=moment(bridgedData['Billing Start Date'],"YYYY-MM-DD").add(moment().diff(moment(bridgedData['Billing Start Date'],"YYYY-MM-DD"), period)+1,period);
          if (paymentPeriod==="Fortnightly") {
            days=moment().diff(moment(bridgedData['Billing Start Date'],"YYYY-MM-DD"), "days");
            rem=days%14;
            days=days-rem+14;
            nextBillingDate=moment(bridgedData['Billing Start Date'],"YYYY-MM-DD").add(days, "days");
          }
        }
        bundle.config.widgets.xdsoftDatepickerRemove({
          element: K("form").find("#datepaymentsresume")[0]
        });          
        period=1;
        span="days";
        if (paymentPeriod==="Weekly"){
          period=7;
        }
        if (paymentPeriod==="Fortnightly"){
          period=14;
        }
        if (paymentPeriod==="Monthly"){
          span="months";
        }
        allowDates=[];
        for (i=0;i<22;i++){
          if (moment(nextBillingDate, "YYYY-MM-DD").add(period*i,span).isAfter(moment())) {
            allowDates[allowDates.length]=moment(nextBillingDate, "YYYY-MM-DD").add(period*i,span).format("DD-MM-YYYY");
          }
        }
        bundle.config.widgets.xdsoftDatepicker({
          element: K("form").find("#datepaymentsresume")[0],
          timepicker: false,
          displayDateFormat: "MM/DD/YYYY",
          options: {
          /*  startDate: tomorrow,
            minDate: 0, */
            allowDates: allowDates, 
            formatDate:"d-m-Y",
            validateOnBlur:false
          },
          onSelectDate:function(ct,$i){
            debugger;
            K('field[Date Payments Resume]').value(ct.dateFormat('YYYY-MM-DD'));
          },
          onGenerate: function( ct ){
            $('.xdsoft_today')
              .addClass('xdsoft_disabled');
            /*  $('.xdsoft_date.xdsoft_weekend')
                    .addClass('xdsoft_disabled'); */
          }                
        });          

        html=dependantHTML.replaceAll("$studentName$", K('field[Student First Name]').value()+" "+K('field[Student Last Name]').value());
        html=html.replaceAll("$memberGUID$", bridgedData['id']);    
        html=html.replaceAll("$parentMemberID$", bridgedData['Billing Parent Member']);    

        $(K('content[Family Dependant Info]').element()).html(html);

        html=nonbillingHTML.replaceAll("$studentName$", K('field[Student First Name]').value()+" "+K('field[Student Last Name]').value());
        html=html.replaceAll("$memberGUID$", bridgedData['id']);    
        $(K('content[Non Billing Info]').element()).html(html);
        }
    });
 }
