bundle.getInt = function(val){
    //So as not to breakup a potential fraction
    let v = parseFloat(val);
    //If we only have the whole:
    if(v % 1 === 0){
      return v;  
    //If the numberis a fraction  
    }else{
      let n = v.toString().split('.').join('');
      return parseInt(n);
    }
  }  
