bundle.storeTermsAndConditions = function(){
  	var startDate=K('field[The first instalment is due on]').value();
  	if (startDate!==null && startDate!=="Invalid date"){
      startDate=moment(startDate,"YYYY-MM-DD").format("L");
      var termsInfo=bundle.termsInformation;
      var schoolName=K('space').attributes('School Name');
      if (schoolName.startsWith("GB ")){
       schoolName=schoolName.replace("GB ",""); 
      }
      if (schoolName.startsWith("Gracie Barra ")){
       schoolName=schoolName.replace("Gracie Barra ",""); 
      }
      var membershipEmail=K('space').attributes('Membership Email');
      var schoolEmail=K('space').attributes('School Email');
      if (membershipEmail===null) membershipEmail=schoolEmail;
      var schoolLegalName=K('space').attributes('School Legal Name');
      var schoolAddress=K('space').attributes('School Address');
      var paymentFreq=K.translate(K('field[Payment Frequency]').value());
      var cancellationDuration=K('space').attributes('Cancellation Duration');
      if (cancellationDuration===undefined || cancellationDuration==="") {
      	cancellationDuration="30";  
      }
      termsInfo=termsInfo.replaceAll("$cancellation_duration$", cancellationDuration);
      termsInfo=termsInfo.replaceAll("$membership_email$", membershipEmail);
      termsInfo=termsInfo.replaceAll("$email$", membershipEmail);
      termsInfo=termsInfo.replaceAll("$school_name$", schoolName);
      termsInfo=termsInfo.replaceAll("$schoolName$", schoolName);
      termsInfo=termsInfo.replaceAll("$school_legal_name$", schoolLegalName);
      termsInfo=termsInfo.replaceAll("$legalName$", schoolLegalName);
      termsInfo=termsInfo.replaceAll("$school_address$", schoolAddress);
      termsInfo=termsInfo.replaceAll("$address$", schoolAddress);
      termsInfo=termsInfo.replaceAll("$state$", K('space').attributes('School State'));
      termsInfo=termsInfo.replaceAll("$billingCompany$", K('space').attributes('Billing Company'));
      termsInfo=termsInfo.replaceAll("$payment_period$", paymentFreq);

      termsInfo=termsInfo.replaceAll("$start_date$", startDate);
      var freezeCharge=K('space').attributes('School Freeze Charge');
      if (K('field[Payment Frequency]').value()==="Fortnightly"){
        freezeCharge="&#163;"+(parseFloat(freezeCharge)/2).toFixed(2);
      } else {
        freezeCharge="&#163;"+freezeCharge;
      }
      termsInfo=termsInfo.replaceAll("$frozen_charge$", freezeCharge);

      K('field[Terms Information Text]').value(termsInfo);  
      $("#termsInformation").html(termsInfo);
    }
}
