K('bridgedResource[Get Member]').load({
  attributes: ['Member ID', 'First Name', 'Last Name'],
  values: {'ID' : 'Allen'},
  success: function(bridgedData) {
    if (bridgedData===null) return;
    bundle.editFeeMode=false; 
    debugger;
    K('field[Member ID]').value(bridgedData['Member ID']);
    K('field[Billing Customer Id]').value(bridgedData['Billing Customer Id']);
    K('field[Billing Customer Reference]').value(bridgedData['Billing Customer Reference']);
    K('field[Student First Name]').value(bridgedData['First Name']);
    K('field[Student Last Name]').value(bridgedData['Last Name']);
    K('field[Payment Frequency]').value(bridgedData['Billing Period']);
    K('field[Billing Period]').value(bridgedData['Billing Period']);
    K('field[Billing Start Date]').value(bridgedData['Billing Start Date']);
    K('field[Biller Migrated]').value(bridgedData['Biller Migrated']);
    K('field[Current Payment Frequency]').value(bridgedData['Billing Period']);
    K('field[Current Payment]').value(bridgedData['Payment']);
    K('field[Current Fee]').value(bridgedData['Admin Fee'].replace("$",""));
    K('field[Payment Total]').value(bridgedData['Payment']);
    K('field[Address]').value(bridgedData['Address']);
    K('field[Suburb]').value(bridgedData['Suburb']);
    K('field[State]').value(bridgedData['State']);
    K('field[Postcode]').value(bridgedData['Postcode']);
    K('field[Email]').value(bridgedData['Email']);
    K('field[Mobile]').value(bridgedData['Mobile']);
    K('field[DOB]').value(bridgedData['DOB']);
    K('field[POS Profile ID]').value(bridgedData['POS Profile ID']);

    K('section[Student Information]').show();

    var paymentPeriod=bridgedData['Billing Period'];
    var period="months";
    if (paymentPeriod==="Daily") {
      period="days";
    } else if (paymentPeriod==="Weekly" || paymentPeriod==="Fortnightly") {
      period="weeks"; 
    } else if (paymentPeriod==="Monthly" || paymentPeriod==="Quarterly" || paymentPeriod==="4 Months" || paymentPeriod==="6 Months") {
      period="months"; 
    } 

    period=1;
    span="days";
    if (paymentPeriod==="Weekly"){
      period=7;
    }
    if (paymentPeriod==="Fortnightly"){
      period=14;
    }
    if (paymentPeriod==="Monthly") {
      period=1;
      span="months";
    }
    if (paymentPeriod==="Quarterly") {
      period=3;
      span="months";
    }
    if (paymentPeriod==="4 Months") {
      period=4;
      span="months";
    }
    if (paymentPeriod==="6 Months") {
      period=6;
      span="months";
    }

    var nextBillingDate=undefined;
    if (moment(bridgedData['Billing Start Date'],"YYYY-MM-DD").isAfter(moment())||moment(bridgedData['Billing Start Date'],"YYYY-MM-DD").isSame(moment().hour(0).minute(0).second(0).millisecond(0))){
      nextBillingDate=moment(bridgedData['Billing Start Date'],"YYYY-MM-DD");
    } else {
      nextBillingDate=moment(bridgedData['Billing Start Date'],"YYYY-MM-DD").add(moment().diff(moment(bridgedData['Billing Start Date'],"YYYY-MM-DD"), period)+1,period);
      if (paymentPeriod==="Fortnightly") {
        days=moment().diff(moment(bridgedData['Billing Start Date'],"YYYY-MM-DD"), "days");
        rem=days%14;
        days=days-rem+14;
        nextBillingDate=moment(bridgedData['Billing Start Date'],"YYYY-MM-DD").add(days, "days");
      } else {
        nextBillingDate=moment(bridgedData['Billing Start Date'],"YYYY-MM-DD").add(period, span);
      }
    }
    K('field[Date Affective]').value(nextBillingDate.format('YYYY-MM-DD'));
    K('field[Billing Next Date]').value(nextBillingDate.format('YYYY-MM-DD'));

    bundle.config.widgets.xdsoftDatepickerRemove({
      element: K("form").find("#dateaffective")[0]
    });          
    bundle.config.widgets.xdsoftDatepickerRemove({
      element: K("form").find("#datepaymentsresume")[0]
    });          
    debugger;
    var tomorrow = new Date((new Date()).getTime() + 24 * 60 * 60 * 1000);
    allowDates=[];
    for (i=0;i<22;i++){
      if (moment(nextBillingDate, "YYYY-MM-DD").add(period*i,span).isAfter(moment())) {
        allowDates[allowDates.length]=moment(nextBillingDate, "YYYY-MM-DD").add(period*i,span).format("DD-MM-YYYY");
      }
    }
    bundle.config.widgets.xdsoftDatepicker({
      element: K("form").find("#dateaffective")[0],
      timepicker: false,
      displayDateFormat: "MM/DD/YYYY",
      options: {
        allowDates: allowDates,
        formatDate:"d-m-Y",
        validateOnBlur:false
      },
      onSelectDate:function(ct,$i){
        K('field[Date Affective]').value(ct.dateFormat('YYYY-MM-DD'));
      },                
      onGenerate: function( ct ){
          $("#dateaffective input").val(moment(K('field[Date Affective]').value(),"YYYY-MM-DD").format("MM/DD/YYYY"));
      }
    });          
    bundle.config.widgets.xdsoftDatepicker({
      element: K("form").find("#datepaymentsresume")[0],
      timepicker: false,
      displayDateFormat: "MM/DD/YYYY",
      options: {
        allowDates: allowDates,
        formatDate:"d-m-Y",
        validateOnBlur:false
      },
      onSelectDate:function(ct,$i){
        K('field[Date Payments Resume]').value(ct.dateFormat('YYYY-MM-DD'));
      },                
      onGenerate: function( ct ){
          $("#datepaymentsresume input").val(K('field[Date Payments Resume]').value()!=null ? moment(K('field[Date Payments Resume]').value(),"YYYY-MM-DD").format("MM/DD/YYYY") : "");
      }    
    });            

    bundle.config.widgets.xdsoftDatepickerRemove({
      element: K("form").find("#newBillingStartDate")[0]
    });          
    debugger;
    var tomorrow = new Date((new Date()).getTime() + 24 * 60 * 60 * 1000);
    bundle.config.widgets.xdsoftDatepicker({
      element: K("form").find("#newBillingStartDate")[0],
      timepicker: false,
      displayDateFormat: "MM/DD/YYYY",
      options: {
        startDate: new Date((new Date()).getTime() + 24 * 60 * 60 * 1000),
        minDate: 0, 
        formatDate:"d-m-Y",
        validateOnBlur:false
      },
      onSelectDate:function(ct,$i){
        K('field[New Billing Start Date]').value(ct.dateFormat('YYYY-MM-DD'));
      },                
      onGenerate: function( ct ){
        $('.xdsoft_today')
          .addClass('xdsoft_disabled');
      }
    });          

    var familyMembers=bridgedData['Billing Family Members'];
    if (familyMembers!=="" && familyMembers!==null && familyMembers!=="[]"){
      if (JSON.parse(familyMembers).length>1) {
	      K('field[Include Family Members]').value("Yes");
      } else {
	      K('field[Include Family Members]').value("No");
      }
      K('field[membersJSON]').value(familyMembers);
      K('field[feesJSON]').value(bridgedData['Family Fee Details']);

      viewerConfig.response = JSON.parse(K('field[feesJSON]').value());
      if (viewerConfig.response!==null) {
	      DataViewer.renderResults($('#memberTableDiv'), viewerConfig);  
      }

      $('#member-table tbody tr').on( 'click', function () {
        if ( $(this).hasClass('selected') ) {
          $(this).removeClass('selected');
        }
        else {
          $('#member-table').DataTable().$('tr.selected').removeClass('selected');
          $(this).addClass('selected');
        }
      });     
      bundle.completeTableChange();      
    } else {
	      K('field[Include Family Members]').value("No");
      viewerConfig.response = JSON.parse("[]");
      if (viewerConfig.response!==null) {
	      DataViewer.renderResults($('#memberTableDiv'), viewerConfig);  
      }

      $('#member-table tbody tr').on( 'click', function () {
        if ( $(this).hasClass('selected') ) {
          $(this).removeClass('selected');
        }
        else {
          $('#member-table').DataTable().$('tr.selected').removeClass('selected');
          $(this).addClass('selected');
        }
      });     
      bundle.completeTableChange();      
    }
    bundle.removedMembers=undefined;


    if (K('space').attributes('Ignore Admin Fee')==="YES" || K('space').attributes('Admin Fee Charge')===null || K('space').attributes('Admin Fee Charge')==="") {
      K('field[Admin Fee]').hide();
      K('field[Admin Fee]').value("");
    } else {
	    K('field[Admin Fee]').show();
      if (K('space').attributes('Admin Fee Label')!==null && K('space').attributes('Admin Fee Label')!=="" && (K('field[Admin Fee]').value()===null || K('field[Admin Fee]').value().indexOf("%")===-1)) {
