bundle.completeTableChange = function(){
  if (K("form").reviewMode()) return;

  K('field[Family Member]').value("");
	$(K("form").find("#selectFamilyMemberMenu [class$='placeholder']")).html("Select...");  				
  K('field[Programs]').value("");
  K('field[tableMemberID]').value("");
  K('field[tableMemberName]').value("");
  K('field[tableProgram]').value("");
  K('field[tableFee]').value("");
  var useDiscounts=K('field[Apply Discounts]').value();
  // Sort table
  viewerConfig.response.sort(function(a, b){return b.cost - a.cost});
  // Let calculate the discounts
  viewerConfig.response.forEach(function(item, index){
    if (item.edited) {
        item.fee=((new Number(item.cost)) - ((new Number(item.cost))*(new Number(item.discount.replace("%","")))/100)).toFixed(2);
    } else {
      if (useDiscounts==="Yes" && index<=bundle.discounts.length-1){
        item.discount=bundle.discounts[index].discount;
        item.fee=((new Number(item.cost))*bundle.discounts[index].cost).toFixed(2);
      } else if (useDiscounts==="Yes" && index>bundle.discounts.length-1 && bundle.discounts.length>0){
        item.discount=bundle.discounts[bundle.discounts.length-1].discount;
        item.fee=((new Number(item.cost))*bundle.discounts[bundle.discounts.length-1].cost).toFixed(2);
      } else {
        item.discount="0%";
        item.fee=((new Number(item.cost))).toFixed(2);
      }
    }
  });
  debugger;
  if (bundle.editFeeMode) {
    // Set payment value
    var feeTotal=0;
    var subTotal=0;
    var total=0;
    viewerConfig.response.forEach(function(item, index){
      feeTotal=feeTotal+(new Number((new Number(item.fee)).toFixed(2)));
    });
    if (feeTotal===0) {
      K('field[Fee Total]').value("0");
      K('field[TAX 1]').value("");
      K('field[TAX 2]').value("");
      K('field[TAX 3]').value("");
      K('field[Payment Total]').value("0");
    } else {  
      if ((K('space').attributes('Ignore Admin Fee')===null || K('space').attributes('Ignore Admin Fee')!=="YES") && K('space').attributes('Admin Fee Charge')!==null && K('space').attributes('Admin Fee Charge')!=="") {  
        var adminFee=K('space').attributes('Admin Fee Charge').replace("%","");

        if (adminFee!=="" && adminFee!==0) {
          var value=(Number(feeTotal)*(Number(adminFee)/100));
          value=new Number(value.toFixed(2));
          K('field[Admin Fee]').value(value);
          subTotal=subTotal+value;
        }
      }
      // Add taxes if configured
      if (bundle.tax1Value!==undefined && bundle.tax1Value!==null && bundle.tax1Value!==""){
        var value=feeTotal*(new Number(bundle.tax1Value));
        value=new Number(value.toFixed(2));
        K('field[TAX 1]').value(value);
        subTotal=subTotal+value;
      }
      if (bundle.tax2Value!==undefined && bundle.tax2Value!==null && bundle.tax2Value!==""){
        var value=feeTotal*(new Number(bundle.tax2Value));
        value=new Number(value.toFixed(2));
        K('field[TAX 2]').value(value);
        subTotal=subTotal+value;
      }
      if (bundle.tax3Value!==undefined && bundle.tax3Value!==null && bundle.tax3Value!==""){
        var value=feeTotal*(new Number(bundle.tax3Value));
        value=new Number(value.toFixed(2));
        K('field[TAX 3]').value(value);
        subTotal=subTotal+value;
      }
      total=feeTotal+subTotal;
      K('field[Payment Total]').value(total!==0 ? total.toFixed(2) : "");
      K('field[Fee Total]').value(feeTotal.toFixed(2));   
    }
  }
  global.viewerConfig.renderer.type($('#memberTableDiv'),global.viewerConfig);

  $('#member-table tbody tr').on( 'click', function () {
    if ( $(this).hasClass('selected') ) {
      $(this).removeClass('selected');
    }
    else {
      $('#member-table').DataTable().$('tr.selected').removeClass('selected');
      $(this).addClass('selected');
    }
  } );  
  if (viewerConfig.response.length===0) {
    K('field[ProgramTableContent]').value("EMPTY");
  }
} 

